mapper.select:
SELECT dc.sido_name, dc.gugun_name, dc.dong_name, d.apt_seq, i.apt_nm FROM house_deals d JOIN house_infos i ON i.apt_seq = d.apt_seq JOIN dong_codes dc ON dc.dong_code = concat(i.sgg_cd, umd_cd)
i.sgg_cd LIKE concat(#{sidoCode}, '%')
AND i.sgg_cd LIKE concat(#{gugunCode}, '%')
AND i.umd_cd = #{dongCode}
AND i.apt_nm LIKE concat("%", #{keyword}, "%")
LIMIT #{pgSize} OFFSET #{offset};

SELECT d.no, d.apt_seq, apt_dong, floor, deal_year, deal_month, deal_day, exclu_use_ar, deal_amount, i.sgg_cd, i.umd_cd, jibun, road_nm_sgg_cd, road_nm, road_nm_bonbun, road_nm_bubun, apt_nm, build_year, latitude, longitude FROM house_deals d JOIN house_infos i ON i.apt_seq = d.apt_seq
i.apt_seq = #{code}
i.sgg_cd = SUBSTRING(#{code}, 1, 5) AND i.umd_cd = SUBSTRING(#{code}, 6, 10)
ORDER BY deal_year desc, deal_month desc, deal_day desc LIMIT #{pgSize} OFFSET #{offset};

SELECT count(d.no) FROM house_deals d JOIN house_infos i ON i.apt_seq = d.apt_seq
i.apt_seq = #{code}
i.sgg_cd = SUBSTRING(#{code}, 1, 5) AND i.umd_cd = SUBSTRING(#{code}, 6, 10)
